#include <stdio.h>
#include <stdlib.h>
void Sequential(int a[],int m,int n){
    int i,j=0;
    for(i=0;i<n;i++){
        if (a[i]==m)  j=i;
    }
    printf("顺序查找结果：%d\n",j+1);
}

void Binary(int a[],int m,int n){
	int low, high, mid;
	low = 0;
	high = n-1;
	int j=0;
	while (low <= high)
	{
		mid = (low + high) / 2;
		if (a[mid] == m){
			j=mid;
			break;
		}
		if (a[mid] > m)
			high = mid - 1;
		if (a[mid] < m)
			low = mid + 1;
	}
    printf("二分查找结果：%d\n",j+1);
}

int main(){
    int m,n,i,num;
    //m表示要查找的数字，n表示数组的长度,num表示要读入的数组数据
    scanf("%d %d",&n,&m);
    int a[n];
    for(i=0;i<n;i++){
        scanf("%d",&a[i]);
    }
    printf("\n"); 
    Sequential(a,m,n);
    Binary(a,m,n);
}

